INSERT INTO db_script_string VALUES
(2000005248, 'All my bags are packed, I\'m ready to go.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL),
(2000005249, 'The ship\'s not ready yet. We still need to calibrate the fuse length to make sure that it doesn\'t burn out and leave you up there alone.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL),
(2000005250, 'You need to do what? All this science. I don\'t understand... Look, this is just my job, five days a week.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL),
(2000005251, 'Essentially, it\'s going to be a long, long time till we are ready to launch. Maybe you should just head back home to your family.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL),
(2000005252, 'I can\'t. I\'m not the man they think I am at home. Besides, I didn\'t bring them out here.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL),
(2000005253, 'Why not? A family can give you strength.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL),
(2000005254, 'This isn\'t the kind of place to raise your kids. It\'s cold, and there\'d be no one to raise them.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL),
(2000005255, 'Couldn\'t you raise them?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL),
(2000005256, 'Oh no, no, no... I\'m a rocket man.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL),
(2000005257, 'Well, it\'s like I said; it\'s going to be a while before we get this ship ready for flight. We\'ll keep you informed.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL);
