INSERT INTO db_script_string VALUES
(2000005271, 'With all due respect, A\'dal sent us here to investigate Kael\'s activities, not mount a full-scaled attack.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL),
(2000005272, 'Look, sweetheart. No one\'s talking about storming Tempest Keep ourselves, but when we see a target of opportunity we have to take it.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL),
(2000005273, 'Hold your tongue, knave! Do not dare use that tone with a priestess of the Light!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL),
(2000005274, 'I\'m surrounded by idiots.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '10', '0', NULL),
(2000005275, 'The magistrix will be happy to know I\'m fluent in Thalassian. Your courtesy is not lost on me.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL),
(2000005276, 'Back to the topic... we should focus our attention on these manaforges. We need to learn what the enemy is doing with them and why.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL),
(2000005277, 'We\'ve told you all there is to know! Do you forget that many of us used to be trusted followers of Kael\'thas?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL),
(2000005278, '...and just look at where that got us!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL),
(2000005279, 'Even you must admit that your information from defectors is outdated and unreliable. Let us make calculated attacks on these manaforges and gather what information we can.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL),
(2000005280, 'Finally some talk of action from the big guy!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '0', NULL);
