INSERT INTO db_script_string VALUES
(2000009052, 'Amakkar, where do you think Warlord Goretooth is going to send us? I don\'t think I can handle another tour in the Steppes.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '6', NULL),
(2000009053, 'Hrmmm...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000009054, 'Quit being such a baby, Razal\'blade. It\'s time to start acting like you\'re part of one of the Warchief\'s elite forces. You don\'t want to end up like Gargal, do you?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000009055, '%s seems oblivious to the nearby commentary.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '2', '0', '0', NULL),
(2000009056, 'Proudtusk!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000009057, 'No, Gargal. Just no.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '274', NULL),
(2000009058, 'But...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000009059, 'NO! Shhh! Just shut up. Goretooth is coming!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '274', NULL),
(2000009060, '%s laughs.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '2', '0', '11', NULL),
(2000009061, 'FALL IN, MAGGOTS!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '5', NULL),
(2000009062, 'All present and accounted for, SIR!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '274', NULL),
(2000009063, 'Very well, Proudtusk, you may begin your march. Carry on!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000009064, 'SIR, YES SIR!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '66', NULL),
(2000009065, 'You heard the Warlord, maggots! MARCH!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '5', NULL),
(2000009066, 'Oh, it\'s just terribly, terribly awful! You will just DIE when you see it, Vivian.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000009067, 'Oh, do tell! The suspense alone is enough to make a girl scream!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000009068, 'Well, from what I hear, the new plague has ALL of the old plague flavor but none of the old plague calories or carbohydrates!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000009069, '%s claps excitedly.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '2', '0', '21', NULL),
(2000009070, 'Oh, how positively delightful!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000009071, 'Well hello there ladies. Heard about the new plague?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '23', NULL),
(2000009072, 'Ewwwww... not if you were the last orc on Kalimdor, Lunk.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '274', NULL),
(2000009073, '%s blushes.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '2', '0', '24', NULL),
(2000009074, '%s laughs.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '2', '0', '11', NULL),
(2000009075, 'Ah, look at the time! I must return to my post... up over there.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL),
(2000009076, 'If you\'ll excuse me, I must take my leave. Good day, ladies.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0', '0', '0', '1', NULL);
