INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES 
(2000001146, 'Listen up, soldiers!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Harbinger Erothem (Entry: 19346)'),
(2000001147, 'You are here because you displayed exceptional aptitude and ability throughout your training period.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Harbinger Erothem (Entry: 19346)'),
(2000001148, 'You are now to be dispatched directly into a hostile environment on a treacherous mission.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Harbinger Erothem (Entry: 19346)'),
(2000001149, 'Nagrand, the land to the west, has recently become littered with enemy forge camps that threaten our security.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Harbinger Erothem (Entry: 19346)'),
(2000001150, 'With the bulk of our forces focused on Illidan, the task of cleansing these lands falls to you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Harbinger Erothem (Entry: 19346)'),
(2000001151, 'You are to return in a week''s time with a detailed report of your actions.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Harbinger Erothem (Entry: 19346)'),
(2000001152, 'May the Light be with you... Dismissed!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Harbinger Erothem (Entry: 19346)'),
(2000001153, 'By your leave, sir, I have pressing matters to attend to.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Harbinger Erothem (Entry: 19346)'),
(2000001154, 'Of course, Harbinger. Dismissed.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 273, 'Adyen the Lightwarden (Entry: 18537)'),
(2000001155, 'Anchorite Nindumen, I have a request to make of you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Harbinger Erothem (Entry: 19346)'),
(2000001156, 'Of course, my friend. How can I be of service to you?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 6, 'Anchorite Nindumen (Entry: 19378)'),
(2000001157, 'We''ve just sent another inexperienced squad into Nagrand. Might you offer a prayer for them?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Harbinger Erothem (Entry: 19346)'),
(2000001158, 'A noble request. It would be an honor, Erothem.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Anchorite Nindumen (Entry: 19378)'),
(2000001159, 'My friends, please join me in humble supplication to the Light.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Anchorite Nindumen (Entry: 19378)'),
(2000001160, 'Light that we embrace, we beseech thee...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Anchorite Nindumen (Entry: 19378)'),
(2000001161, 'Into our struggle against the darkness we have sent our children.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Anchorite Nindumen (Entry: 19378)'),
(2000001162, 'Please bless and protect them, and grant them success over those who seek to do us harm...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Anchorite Nindumen (Entry: 19378)'),
(2000001163, 'Continue to bless this, our ancient home, we beg.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Anchorite Nindumen (Entry: 19378)'),
(2000001164, 'Bless us with tolerance for our elven visitors.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Anchorite Nindumen (Entry: 19378)'),
(2000001165, 'By the Naaru, may it be so.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Anchorite Nindumen (Entry: 19378)'),
(2000001166, 'May it be so.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Praying Aldor NPCs'),
(2000001167, 'Sir, I am here to deliver my report from the Scryer''s Tier.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Neophyte Nemarn (Entry: 19377)'),
(2000001168, 'Well, out with it, neophyte...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Adyen the Lightwarden (Entry: 18537)'),
(2000001169, 'Sir, it''s impossible to read anything into their actions.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Neophyte Nemarn (Entry: 19377)'),
(2000001170, 'Their movements are totally chaotic, as if they themselves know not what they are about.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Neophyte Nemarn (Entry: 19377)'),
(2000001171, 'My apologies, sir. I wish I had more to report.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Neophyte Nemarn (Entry: 19377)'),
(2000001172, 'It is exactly as I expected, soldier. They know we watch them closely.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 273, 'Adyen the Lightwarden (Entry: 18537)'),
(2000001173, 'They mask their intentions with meaningless movements to confuse us.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Adyen the Lightwarden (Entry: 18537)'),
(2000001174, 'In time, you may learn to see through their deceptions.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Adyen the Lightwarden (Entry: 18537)'),
(2000001175, 'Until then, your presence at your post will keep them on their toes.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Adyen the Lightwarden (Entry: 18537)'),
(2000001176, 'As you command, sir.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 273, 'Neophyte Nemarn (Entry: 19377)');
