INSERT INTO db_script_string (entry, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8) VALUES
(2000006000,'I\'m through asking nicely, fish-man.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
(2000006001,'Where is the Cipher of Damnation? I know a third of it is in this village.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
(2000006002,'Whhaaat... are you sssspeaking of, ssssoft-sskin?',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
(2000006003,'The naga do nottt... have thissss... cipherrr...',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
(2000006004,'No... Of course not. What was I thinking? Why would you have a third of the Cipher of Damnation?',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
(2000006005,'I feel so stupid. Maybe I should unchain you and set you free?',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
(2000006006,'Yesss... Yesss... Ssset me freee...',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
(2000006007,'DO I HONESTLY LOOK THAT STUPID TO YOU, NAGA? NOW YOU FEEL PAIN!',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
(2000006008,'%s spits on the Coilskar naga.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
(2000006009,'GET UP YOU ROTTEN PIECE OF TRASH! GET UP BEFORE I END YOU!',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
(2000006010,'%s regains consciousness.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
(2000006011,'I\'m listening..',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
(2000006012,'Do not beat the Coilsssskaarrr commander anymore, orc! I will tell you! The cipherrr... It is hidden in one of the chests at Coilssskarrr Point... The naga there hold the keysss...',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
(2000006013,'COILSSSKARRR!',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
